import SwiftUI
import WebKit
import LoremSwiftum

struct ContentView: View {
    var html: String
    
    var body: some View {
        WebViewWrapper(html: html)
            .frame(width: 400)
            .frame(minHeight: 800, idealHeight: 800, maxHeight: 2000)
            .edgesIgnoringSafeArea(.all)
        
    }
    
    init(){
        var resume = Resume()
        RandomGenerator.randomizeContent(&resume)
        resume.style.inset = 20
        
        
        resume.sections = [ResumeSection(title: "", style: .detailled)]
        var experience = RandomGenerator.randomExperience()
        experience.description = Lorem.sentences(2)
        experience.title = "Job title"
        experience.address = "Location"
        experience.company = "Company name"
        resume.sections[0].items = [experience]
        
        resume.profile.name = "John Doe"
        resume.profile.designation = "Consultant"
        
        resume.contact.email = "john@email.com"
        resume.contact.phone = "1-222-333-4444"
        resume.contact.lines = [ResumeContactLine(id: UUID(), label: "website", value: "https://website.com")]
        //-------------------
        //let template = "test-contacts.html"
        //-------------------
        let template = "test-titles.html"
        //-------------------
        //let template = "test-sections.html"
        //resume.sections = []
        //-------------------
        //let template = "test-items.html"
        //-------------------
        //let template = "test-layouts.html"
        
        self.html = ResumeGenerator.shared.getHTML(resume: resume, forcedLayout: template)
        print(self.html)
    }
    
    static func fakePortrait() -> String {
        return ""
    }
}

struct ContentView_Previews: PreviewProvider {
    static var previews: some View {
        ContentView()
    }
}

struct WebViewWrapper: NSViewRepresentable {
    let html: String
    
    func makeNSView(context: Context) -> WKWebView {
        return WKWebView()
    }
    
    func updateNSView(_ nsView: WKWebView, context: Context) {
        nsView.loadHTMLString(html, baseURL: nil)
    }
}
